class SolutionTimeOut {
    public boolean isPossible(int[] nums) {
        // ç›®å‰çœ‹æ¥, ä»…ä½¿ç”¨è´ªå¿ƒç®—æ³•ä¼¼ä¹å°±èƒ½æ”¯æ’‘
        // ä¸€éæ‰«æ,å½“å‰æ•°å­—ä¸ºn, å¦‚æœç»“æœæ± ä¸­æœ‰ä»¥ n-1ç»“å°¾çš„æ•°ç»„, é€‰é•¿åº¦æœ€çŸ­çš„è¿½åŠ , å¦‚æœæ²¡æœ‰, å‘ç»“æœæ± æ–°å†™å…¥ä¸€ä¸ªæ•°ç»„
        List<List<Integer>> pool = new ArrayList<>();
        int count = 0;
        for (int item : nums) {
            List<Integer> head = null;
            // éå†æ³³æ± 
            for (List<Integer> temp : pool) {
                int tempSize = temp.size();
                // å¦‚æœèƒ½è¿½åŠ !
                if (temp.get(tempSize - 1) == item - 1) {
                    if (head == null || tempSize < head.size()) {
                        head = temp;
                    }
                }
            }
            if (head == null) {
                count++;
                head = new ArrayList<>();
                pool.add(head);
            } else if (head.size() == 2) {
                count--;
            }
            head.add(item);
        }
        return count == 0;
    }
}
